import React, { useRef } from 'react'
import { useHistory } from 'react-router';
import { auth } from '../firebase';
import "./SignInScreen.css"

function SignInScreen() {

  const firstName = useRef(null);
  const LastName = useRef(null);
  const passwordRef = useRef(null);

  const history = useHistory();

  const signIn = (e) => {
    e.preventDefault();

    auth.signInWithEmailAndPassword(
      emailRef.current.value,
      passwordRef.current.value
    )
    .then((authUser) => {
      console.log(authUser)
    })
    .catch(error => {
      alert(error.message)
    })
  }

  return (
    <div className="signinScreen">
      <form>
        <h1>Sign In</h1>
        <input ref={firstName} placeholder="firstName" type="text" />
        <input ref={LastName} placeholder="LastName" type="text" />
        <input ref={passwordRef} placeholder="Password" type="password" />
        <button type="submit" onClick={signIn}>Sign Up</button>

        <h4> 
          <span className="signinScreen__gray"> Already a user?  </span> 
          <span className="signinScreen__link" onClick={() => history.push("./signup")}>Sign Up now.</span>
        </h4>
      </form>
    </div>
  )
}

export default SignInScreen
