import { configureStore } from '@reduxjs/toolkit';
import userReducer from '../features/userSlice';

/* configureStore is a redux toolkit that adds good defaults to the store setup for a better development experience */
export const store = configureStore({
  reducer: {
    user: userReducer,
  },
});
