import React, { useEffect, useState } from 'react'
import './Banner.css'
import axios from "./axios"
import requests from "./Requests"
import {API_KEY} from './Requests'
import YouTube from 'react-youtube'


function Banner() {

  const [movie, setMovie] = useState([]);
  const [trailerUrl, setTrailerUrl] = useState('');

  useEffect(()=> {
    async function fetchData(){
      const request = await axios.get(requests.fetchNetflixOriginals);

      setMovie(
        request.data.results[
          Math.floor(Math.random() * request.data.results.length - 1)
        ]
      );
      return request;
    }

    fetchData();
  }, [])

  const opts = {
    height: "100%",
    width: "100%",
    playerVars: {
      autoplay: 1,
    } 
  }

  const playtrailer = async(movie) => {
    let trailerurl = await axios.get(
      `/movie/${movie.id}/videos?api_key=${API_KEY}`
    );
    setTrailerUrl(trailerurl.data.results[0]?.key);

    return(
    <YouTube videoId = {trailerUrl} opts={opts}/>)
  }

  function truncate(string, n){
    //for cutting off the movie description and ends it with a ...
    //we need to use string with a ? as there may not always be a string being passed, some may be undefined
    return string?.length > n ? string.substr(0, n-1) + '...' : string;
  }

  return (
    <header 
      onMouseMove={() => playtrailer(movie)}
      className="banner" 
      //doing an inline styling
      style={{
      backgroundSize: "cover",
      backgroundImage: `url("https://image.tmdb.org/t/p/original/${movie?.backdrop_path}")`,
      backgroundPosition: "center center"}}
      >
      <div className="banner__contents">
        <h1 className="banner__title">
          {movie?.title || movie?.name || movie?.orginal_name}
        </h1>
        <div className="banner__buttons">
          <button className="banner__button">Play</button>
          <button className="banner__button">My List</button>
        </div>
        <h1 className="banner__description">
          {truncate(movie?.overview, 100)}
        </h1>
      </div>

    </header>
  )
}

export default Banner
